import detect from './detect';
import deviceMatrix from './device-matrix.json';
var {
  browser,
  os,
  bots
} = detect;
/**
 * Returns if the current browser is supported by the web app
 * The algorithm is inclusive, so we enumerate everything we don't support, after that everything is supported
 *
 * @returns {Boolean} supported
 */

function isSupported() {
  var userBrowser = browser.getName().toLowerCase();
  var userBrowserVersion = parseFloat(browser.getVersion());
  var userOS = os ? os.getName().toLowerCase() : 'unknown';
  var userOSVersion = os.getVersion() === 'Unknown' ? Number.MAX_SAFE_INTEGER : parseFloat(os.getVersion());

  if (bots.isBot()) {
    return true;
  }

  var supportedOsVersion = deviceMatrix[userOS];
  var supportedBrowserVersion = deviceMatrix[userBrowser];
  var supportedVersion = supportedBrowserVersion || supportedOsVersion;

  if (supportedVersion) {
    if (supportedOsVersion !== undefined) {
      if (userOSVersion < supportedOsVersion) {
        return false;
      }
    }

    if (supportedBrowserVersion !== undefined) {
      /* Special contrains here */
      if (userBrowserVersion < supportedBrowserVersion) {
        return false;
      }
    }
  }

  return true;
}
/**
 * Print current browser information
 *
 * @returns {String} Browser|Browser Version|OS|OS Version|UA
 */


function getBrowserInformation() {
  return "\n    browser: ".concat(browser.getName(), ",\n    browser version: ").concat(browser.getVersion(), ",\n    os: ").concat(os.getName(), ",\n    os version: ").concat(os.getVersion(), ",\n    ua: ").concat(navigator.userAgent, "\n  ");
}

